<script lang="ts">
	import { onMount } from "svelte"
	import { beepAudio } from "$lib"
	import { changeState } from "$lib/clock-state"
	import { page } from "$app/stores"
	import { _ } from "svelte-i18n"
  import { pwaInfo } from "virtual:pwa-info"

	import "fluent-svelte/theme.css"
	import "../styles/_global.scss"

	const handleKeys = async (e: KeyboardEvent) => {
		if ($page.route.id !== "/end" && (e.key === "Enter" || e.key === " " || e.key === "Tab")) {
			e.preventDefault()

			if ($page.route.id === "/") {
				// going to walkup
				await beepAudio.playBeep(2)
			} else if ($page.route.id === "/walkup") {
				// starting an end
				await beepAudio.playBeep()
			}

			changeState($page)
		}
	}

	onMount(async () => {
		if (pwaInfo) {
			const { registerSW } = await import("virtual:pwa-register")
			registerSW({
				immediate: true,
				onRegistered(r) {
					// uncomment following code if you want check for updates
					// r && setInterval(() => {
					//    console.log('Checking for sw update')
					//    r.update()
					// }, 20000 /* 20s for testing purposes */)
					console.log(`SW Registered: ${r}`)
				},
				onRegisterError(error) {
					console.log("SW registration error", error)
				},
			})
		}
	})

	$: webManifest = pwaInfo ? pwaInfo.webManifest.linkTag : ""

	onMount(() => {
		// audio permalink:
		// https://sfxr.me/#57uBnWkAfvbhTZQJAyEjTczWfcRpQqAS6rvcTvCsEwUeWsFRVdaFcrDvaUXT1r2H7jCSoH4vvCYWL3YGBJaeyXmoCHYZeM2m7ibTLWyH3isMv3KX8ny7MZRYT
		beepAudio.beep = new Audio(
			"data:audio/x-wav;base64,"
)
	})
</script>

<svelte:window on:keydown={handleKeys} />

<svelte:head>
  <title>{$_("title")}</title>
	<meta content={$_("title")} name="title" />
	<meta content={$_("title")} name="og:title" />
  {@html webManifest}
</svelte:head>

<slot />
